    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here
       String str = null;
if (jRadioButton1.isSelected()){str="YOur date is set  place : "+jComboBox1.getItemAt(jComboBox1.getSelectedIndex())+"\n";
str= str+"date  :"+jComboBox2.getItemAt(jComboBox1.getSelectedIndex())+" am";
JOptionPane.showMessageDialog(this,str , "Confirm",JOptionPane.QUESTION_MESSAGE );}
else{str="YOur date is set  place : "+jComboBox1.getItemAt(jComboBox1.getSelectedIndex())+"\n";
str= str+"date  :"+jComboBox2.getItemAt(jComboBox1.getSelectedIndex())+" pm";
JOptionPane.showMessageDialog(this,str , "Confirm",JOptionPane.QUESTION_MESSAGE );}
     
    }//GEN-LAST:event_jButton1ActionPerformed

